export default {
    name: "resetpasswords",
    data(){
        var testpassword = (rule, value, callback) =>{
            const pat0=/^[\w-_]{6,32}$/;//密码的规则0
            const pat1=/^[0-9]{6,32}$/;//密码的规则1
            const pat2=/^[-_]{6,32}$/;//密码的规则2
            const pat3=/^[a-zA-Z]{6,32}$/;//密码的规则3
            if(pat1.test(value)||pat2.test(value)||pat3.test(value)){
               return callback(new Error('*密码需长度为6-32位，且字母，数字或者特殊字符（-_）至少包含两种'));
            }
            else {
                if(pat0.test(value)){
                    return callback();
                }
                else {
                    return callback(new Error('*密码需长度为6-32位，且字母，数字或者特殊字符（-_）至少包含两种'));
                }
            }
        }

        var WhetherSame = (rule, value, callback) =>{
            if(this.editRules.pw2==this.editRules.pw1){
                return callback();
            }
            else{
                return callback(new Error('*请两次输入的密码保持一致'));
            }
        }
        return{
            ruleForm:{},
            editRules :({
                pw1: [
                    {required: true, message: '请输入新密码', trigger: 'blur' },
                    {validator: testpassword,trigger: 'blur'}
                ],
                pw2: [
                    {required: true, message: '请再次确认密码', trigger: 'blur',},
                    {validator: WhetherSame,trigger: 'blur'}
                ],
            }),
        }
    },

    created(){
    },
    methods:{
    }
}
